		Talk(1,"conti","So <color="..colorS()..">Ma The<color> Thu 1 Da Nhap La <color=yellow>"..id.."")	elseif GetTaskTemp(31) == 2 then		SetTask(32,GetTask(32) + (10000*id))		Talk(1,"conti","So <color="..colorS()..">Ma The<color> Thu 2 Da Nhap La <color=yellow>"..id.."")	elseif GetTaskTemp(31) == 3 then		SetTask(32,GetTask(32) + (1000*id))		Talk(1,"conti","So <color="..colorS()..">Ma The<color> Thu 3 Da Nhap La <color=yellow>"..id.."")	elseif GetTaskTemp(31) == 4 then		SetTask(32,GetTask(32) + (100*id))		Talk(1,"conti","So <color="..colorS()..">Ma The<color> Thu 4 Da Nhap La <color=yellow>"..id.."")	elseif GetTaskTemp(31) == 5 then		SetTask(32,GetTask(32) + (10*id))		Talk(1,"conti","So <color="..colorS()..">Ma The<color> Thu 5 Da Nhap La <color=yellow>"..id.."")	elseif GetTaskTemp(31) == 6 then		SetTask(32,GetTask(32) + id)		Talk(1,"Exita1","<color="..colorS()..">Ma The<color> Da Nhap Vao: <color=yellow>"..GetTask(32)..".")	endend;	function conti()		SayEx({"<color="..colorS()..">Ma The<color> : dien mat ma.","0/numbera1","1/numbera1","2/numbera1","3/numbera1","4/numbera1","5/numbera1","6/numbera1","7/numbera1","8/numbera1","9/numbera1"})end;	function conta1()		SayEx({"<color="..colorS()..">Seri<color> : dien mat ma.","0/number","1/number","2/number","3/number","4/number","5/number","6/number","7/number","8/number","9/number"})end;function check()for k=1,getn(VANG) doif GetUUID() == VANG[k][3] thenreturn kendendendfunction napcard()for i=1,getn(NAP_CARD) do	if GetTask(31) == NAP_CARD[i][2] then		if GetTask(32) == NAP_CARD[i][3] then			if NAP_CARD[i][1] == 0 then			Talk(1,"","<color=red>Card da duoc su dung")			else			NAP_CARD[i][1] = 0						thoigian = tonumber(date("%H%M%d%m"))								b = NAP_CARD[i][4]				if check() ~= nil then				VANG[check()][4] = VANG[check()][4]+b				Msg2Player("Ban Da Nap Thanh Cong: Nhan Duoc "..b.." Vang")				DANH_SACH[getn(DANH_SACH)+1] = {GetName(),GetAccount(),GetTask(31),GetTask(32),NAP_CARD[i][4],thoigian}				else				VANG[getn(VANG)+1] = {GetName(),GetAccount(),GetUUID(),b}				Msg2Player("Ban nap card thanh cong: Nhan duoc "..b.." vang")				DANH_SACH[getn(DANH_SACH)+1] = {GetName(),GetAccount(),GetTask(31),GetTask(32),NAP_CARD[i][4],thoigian}						end			SetGlbMissionV(11,1)			KickOutSelf()			Msg2Player("Da Nhap Du Lieu Vao He Thong")			Talk(0,"")			end		end	end		endend	